    ["Authentication.TwoStepVerification.Action.Reload"] = [[Erneut laden]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationItemTradeEmail.V2.Body"] = [[{paragraphStart}Hallo,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} ist dein Roblox-Code für die zweistufige Verifizierung für {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd}{paragraphStart}Gib den obigen Code im Bildschirm zur zweistufigen Verifizierung ein, um den Handel fertig zu verifizieren. Dieser Code wird in 15 Minuten ungültig.{paragraphEnd}{paragraphStart}Diese Anmelde-Anfrage wurde in {emphasisStart}{geoLocationInformation}{emphasisEnd} gesendet. {paragraphEnd}{lineBreak}{paragraphStart}Vielen Dank{lineBreak}{emphasisStart}Das Roblox-Team{emphasisEnd}{paragraphEnd}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationItemTradeEmail.V2.Subject"] = [[Roblox Handelsverifizierungs-Anfrage: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationItemTradeEmail.V2.Footer"] = [[Antworte nicht direkt auf diese E-Mail. Du erhältst diese E-Mail, weil es erhöhte Handelsaktivitäten auf Roblox gab. Wenn du das nicht warst, raten wir dir dringend, dein Passwort zu ändern.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationResaleEmail.V1.Subject"] = [[Roblox Wiederverkaufs-Verifizierungsanfrage: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationResaleEmail.V1.Footer"] = [[Antworte nicht direkt auf diese E-Mail. Du erhältst diese E-Mail, weil es erhöhte Wiederverkaufsaktivitäten gab. Wenn du das nicht warst, raten wir dir dringend, dein Passwort zu ändern.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationResaleEmail.V1.Body"] = [[{paragraphStart}Hallo,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} ist dein Roblox-Code für die zweistufige Verifizierung für {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd}{paragraphStart}Gib den obigen Code im Bildschirm für die zweistufige Verifizierung ein, um den Wiederverkauf fertig zu verifizieren. Dieser Code wird in 15 Minuten ungültig.{paragraphEnd}{paragraphStart}Diese Anfrage wurde in {emphasisStart}{geoLocationInformation}{emphasisEnd} gesendet. {paragraphEnd}{lineBreak}{paragraphStart}Vielen Dank{lineBreak}{emphasisStart}Das Roblox-Team{emphasisEnd}{paragraphEnd}]],
    ["Authentication.TwoStepVerification.Label.TwoStepRequired"] = [[Zweistufige Verifizierung ist erforderlich, um weitere Transaktionen zu tätigen. Gehe zu Einstellungen > Sicherheit, um die Einrichtung der zweistufigen Verifizierung abzuschließen.]],
    ["Authentication.TwoStepVerification.Title.VerificationRequired"] = [[Verifizierung erforderlich]],
    ["Authentication.TwoStepVerification.Action.GoToSecurity"] = [[Einstellungen]],
    ["Authentication.TwoStepVerification.Label.TwoStepRequiredLogout"] = [[Zweistufige Verifizierung ist für weitere Transaktionen erforderlich. Gehe zu Einstellungen > Sicherheit, um die Einrichtung der zweistufigen Verifizierung abzuschließen. Durch die Aktivierung der zweistufigen Verifizierung wirst du von deiner aktuellen Sitzung abgemeldet.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationPasswordResetEmail.V1.Body"] = [[{paragraphStart}Hallo,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} ist dein Roblox-Code für die zweistufige Verifizierung für {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd}{paragraphStart}Gib den obigen Code im Bildschirm für die zweistufige Verifizierung ein, um mit der Zurücksetzung deines Passworts fortzufahren. Dieser Code wird in 15 Minuten ungültig.{paragraphEnd}{paragraphStart}Diese Anfrage wurde in {emphasisStart}{geoLocationInformation}{emphasisEnd} gesendet. {paragraphEnd}{lineBreak}{paragraphStart}Vielen Dank{lineBreak}{emphasisStart}Das Roblox-Team{emphasisEnd}{paragraphEnd}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationPasswordResetEmail.V1.Footer"] = [[Antworte nicht direkt auf diese E-Mail. Du erhältst diese E-Mail, da versucht wurde, dein Passwort zurückzusetzen.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationPasswordResetEmail.V1.Subject"] = [[Anfrage zur Roblox-Passwort-Zurücksetzung: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSMSLogin.Body"] = [[Roblox zweistufige Verifizierung für {accountName}: Bitte gib {code} ein, um dein Konto zu verifizieren]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSMSActivationText.Body"] = [[Roblox zweistufige Verifizierung für {accountName}: Bitte gib {code} ein, um zweistufige Verifizierung zu aktivieren.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSMSDeactivationText.Body"] = [[Roblox zweistufige Verifizierung für {accountName} deaktiviert.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSMSCustomerSupportDeactivationText.Body"] = [[Der Kundensupport hat die zweistufige Verifizierung für dein Roblox-Konto deaktiviert. Dies geschieht in der Regel aufgrund eines von dir hinterlegten Support-Tickets oder als Sicherheitsmaßnahme, um die Sicherheit deines Kontos zu gewährleisten. Ein Sicherheitscode über SMS wird für einige Aktionen wie das Einloggen nicht mehr benötigt. Du kannst zweistufige Verifizierung in den Kontoeinstellungen unter „Sicherheit“ reaktivieren.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSMSRecoveryCodeDepletedText"] = [[Du hast keine 2SV-Konto-Wiederherstellungscodes für dein Roblox-Konto mehr übrig. Bitte kontaktiere den Kunden-Support für weitere Unterstützung bei der Wiederherstellung deines Kontos.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSMSEnabledText.Body"] = [[Roblox zweistufige Verifizierung für {accountName} deaktiviert.]],
    ["Authentication.TwoStepVerification.Label.SmsMediaType"] = [[SMS]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRevertAccountEmail.V1.Body"] = [[{paragraphStart}Hallo,{paragraphEnd}{paragraphStart}{emphasisStart}{code}{emphasisEnd} ist dein Roblox-Code für die zweistufige Verifizierung für {emphasisStart}{accountName}{emphasisEnd}.{paragraphEnd}{paragraphStart}Gib den obigen Code im Bildschirm für die zweistufige Verifizierung ein, um die Änderungen an deinem Konto weiter rückgängig zu machen. Dieser Code wird in 15 Minuten ungültig.{paragraphEnd}{paragraphStart}Diese Anfrage wurde in {emphasisStart}{geoLocationInformation}{emphasisEnd} gesendet. {paragraphEnd}{lineBreak}{paragraphStart}Vielen Dank{lineBreak}{emphasisStart}Das Roblox-Team{emphasisEnd}{paragraphEnd}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRevertAccountEmail.V1.Footer"] = [[Antworte nicht direkt auf diese E-Mail. Du erhältst diese E-Mail, da versucht wurde, Änderungen an deinem Konto rückgängig zu machen.]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationRevertAccountEmail.V1.Subject"] = [[Anfrage zur Rückgängigmachung von Änderungen auf Roblox: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationText.Body.Over13"] = [[Hallo {accountName},{lineBreak}{lineBreak}du hast die zweistufige Verifizierung per SMS für dein Roblox-Konto aktiviert. Für einige Aktionen, wie z.B. die Anmeldung, muss ein 6-stelliger Sicherheitscode, den Roblox per SMS sendet, eingegeben werden.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationSMS.Body.Under13"] = [[Hallo,{lineBreak}{lineBreak} zweistufige Verifizierung per SMS für das Roblox-Konto deines Kindes, {accountName}, ist aktiviert. Für einige Aktionen, wie z.B. die Anmeldung, muss ein 6-stelliger Sicherheitscode, den Roblox per SMS sendet, eingegeben werden.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationText.Body.Under13"] = [[Hallo,{lineBreak}{lineBreak} zweistufige Verifizierung per SMS für das Roblox-Konto deines Kindes, {accountName}, ist aktiviert. Für einige Aktionen, wie z.B. die Anmeldung, muss ein 6-stelliger Sicherheitscode, den Roblox per SMS sendet, eingegeben werden.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationActivationText.Subject"] = [[Zweistufige Verifizierung per SMS ist für das folgende Roblox-Konto aktiviert: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationText.Subject"] = [[Zweistufige Verifizierung per SMS wurde vom Roblox-Support deaktiviert für: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationText.Body.Over13"] = [[Hallo {accountName},{lineBreak}{lineBreak}du hast die zweistufige Verifizierung per SMS für dein Roblox-Konto deaktiviert.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationText.Body.Under13"] = [[Hallo,{lineBreak}{lineBreak}die zweistufige Verifizierung per SMS wurde für das Roblox-Konto deines Kindes, {accountName}, deaktiviert.{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationDeactivationText.Subject"] = [[Zweistufige Verifizierung per SMS ist für das folgende Roblox-Konto deaktiviert: {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationText.HtmlBody.Over13"] = [[Hallo {accountName},{lineBreak}{lineBreak}wir möchten dir mit dieser Nachricht mitteilen, dass unser Support-Team die zweistufige Verifizierung per SMS für dein Roblox-Konto deaktiviert hat. Das geschieht normalerweise, wenn du eine Nachricht an den Support geschickt hast oder als Vorsichtsmaßnahme, um die Sicherheit deines Kontos zu gewährleisten. Für einige Aktionen, wie z.B. die Anmeldung, ist kein Sicherheitscode mehr nötig. Du kannst zweistufige Verifizierung in den {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}Kontoeinstellungen{aTagEnd} jederzeit erneut aktivieren.{lineBreak}{lineBreak}Solltest du Hilfe brauchen, {spanStartTagWithBold}antworte nicht auf diese E-Mail,{spanEndTag} sondern antworte direkt auf die E-Mail vom Support-Team oder reiche hier eine neue Support-Anfrage ein: {aTagStartWithHref}{supportPageLink}{hrefEnd}Roblox Support{aTagEnd}{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationCustomerSupportDeactivationText.HtmlBody.Under13"] = [[Hallo,{lineBreak}{lineBreak}wir möchten dir mit dieser Nachricht mitteilen, dass unser Support-Team die zweistufige Verifizierung per SMS für das Roblox-Konto deines Kindes, {accountName}, deaktiviert hat. Das geschieht normalerweise, wenn du eine Nachricht an den Support geschickt hast oder als Vorsichtsmaßnahme, um die Sicherheit des Kontos zu gewährleisten. Für einige Aktionen, wie z.B. die Anmeldung, ist kein Sicherheitscode mehr nötig. Du kannst zweistufige Verifizierung in den {aTagStartWithHref}{AccountSettingsPageLink}{hrefEnd}Kontoeinstellungen{aTagEnd} jederzeit erneut aktivieren.{lineBreak}{lineBreak}Solltest du Hilfe brauchen, {spanStartTagWithBold}antworte nicht auf diese E-Mail,{spanEndTag} sondern antworte direkt auf die E-Mail vom Support-Team oder reiche hier eine neue Support-Anfrage ein: {aTagStartWithHref}{supportPageLink}{hrefEnd}Roblox Support{aTagEnd}{lineBreak}{lineBreak}Roblox]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSwap.Subject"] = [[Änderung bei der zweistufigen Verifizierung für {accountName}]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSwap.Body.Over13"] = [[Hallo,{lineBreak}{lineBreak}du hast die Methode zur zweistufigen Verifizierung für {accountName} von {disabledTwoStepMethod} zu {enabledTwoStepMethod} geändert. Wenn diese Änderung nicht von dir vorgenommen wurde, kontaktiere bitte sofort den Kundensupport, um das Konto wiederherzustellen.{lineBreak}{lineBreak}Danke{lineBreak}{lineBreak}Roblox-Team]],
    ["Authentication.TwoStepVerification.Description.TwoStepVerificationSwap.Body.Under13"] = [[Hallo,{lineBreak}{lineBreak}die Methode zur zweistufigen Verifizierung für das Konto deines Kindes wurde {accountName} von {disabledTwoStepMethod} zu {enabledTwoStepMethod} geändert. Wenn diese Änderung nicht von dir oder deinem Kind vorgenommen wurde, kontaktiere bitte sofort den Kundensupport, um das Konto deines Kindes wiederherzustellen.{lineBreak}{lineBreak}Danke{lineBreak}{lineBreak}Roblox-Team]],
    ["Authentication.TwoStepVerification.Label.VerifyWithSecurityKey"] = [[Verwende deinen Sicherheitsschlüssel um dich anzumelden.]],
    ["Authentication.TwoStepVerification.Label.SecurityKeyMediaType"] = [[Sicherheitsschlüssel]],
    ["Authentication.TwoStepVerification.Label.UseSecurityKey"] = [[Verwende deinen Sicherheitsschlüssel, um dich anzumelden.{lineBreak}Wenn du dazu aufgefordert wirst, berühre deinen Schlüssel oder verwende biometrische Daten auf diesem Gerät.]],
