	-- Remember: these lines are equivalent
	["message"] = "succes";
	message = "success";

	info = {
		points = 120,
		isLeader = true,
		user = {
			id = 12345,
			name = "JohnDoe"
		},
		past_scores = {50, 42, 95},
		best_friend = nil
	}
}

